import { createSlice } from "@reduxjs/toolkit";
import axios from "axios"
import { createAsyncThunk } from "@reduxjs/toolkit";
const initialState={
  datas:[],
  isloading:false,
  error:null
}


export const fetchLocalData = createAsyncThunk('fetchLocalData', async () => {
    const response = await axios.get('/data.json');
    return response;
  });

  export const localUserSlice=()=>createSlice({
    name:"LocalUsers",
    initialState,
    reducers:{},
    extraReducers:(builder)=>{
        builder.addCase(fetchLocalData.pending,(state,action)=>{
            state.isloading=true;
        })

        builder.addCase(fetchLocalData.fulfilled,(state,action)=>{
            state.datas=action.payload
            state.isloading=false;
        })

        builder.addCase(fetchLocalData.rejected,(state,action)=>{
            state.isloading=false;
            state.error=action.error.message;
        })
    
    
    }
  })
  export default localUserSlice.reducer;
