import React,{useState,useEffect} from 'react';
import './App.css';
import CounterSlice from './features/counter/counterSlice';
import { Counter } from './features/counter/Counter';
import { useSelector,useDispatch } from 'react-redux';
import { addUser, deleteUser,updateUser } from "./features/Users";
console.log(addUser())
function App() {
  const users=useSelector((state)=>state.users.value)//reducerın,state değerini erişim
  const dispatch=useDispatch();//reducer a ilgili action ı iletir ,reducer action a göre statein durumunu günceller.
  console.log(users.length)
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [newUsername, setNewUsername] = useState("");

  return (
    <div className="App">


  <div>
    <input type="text" placeholder='Name' value={name} onChange={(e) => {setName(e.target.value);
          }}/>
    <input type="text" placeholder='UserName'  value={username} onChange={(e) => {setUsername(e.target.value);}}/>
    <button onClick={()=>dispatch(addUser({id:users.length+1,name:name,username:username}))}>Add</button>
  </div>

{users.map((user)=>(
  <div key={user.id}>
    <div>{user.name} 
    <span>{user.username}</span>
   <p>
   <input type="text" placeholder={user.name} value />
    <input type="text" placeholder={user.username} />
   </p>
   <p>
   <button onClick={()=>dispatch(deleteUser({id:user.id}))}>Delete</button>
   <button onClick={()=>dispatch(updateUser({}))}>Update</button>
   </p>
    </div>
  </div>
))}
    </div>
  );
}

export default App;
